// T071: Prepare download requests for spec.md, plan.md, tasks.md
const data = $input.first().json;

return [{
  json: {
    task_id: data.task_id,
    current_task_number: data.current_task_number || 1,
    feedback: data.feedback || null,
    downloads: {
      spec: { operation: 'download', container: 'agent-spec', blob_path: `${data.task_id}/spec.md` },
      plan: { operation: 'download', container: 'agent-spec', blob_path: `${data.task_id}/plan.md` },
      tasks: { operation: 'download', container: 'agent-spec', blob_path: `${data.task_id}/tasks.md` }
    }
  }
}];

// Extract spec download request
const data = $input.first().json;
return [{ json: data.downloads.spec }];

// Extract plan download request
const data = $('Prepare Downloads').first().json;
return [{ json: data.downloads.plan }];

// Extract tasks download request
const data = $('Prepare Downloads').first().json;
return [{ json: data.downloads.tasks }];

// T072: Parse current task from tasks.md based on task_number
const items = $input.all();
const inputData = $('Prepare Downloads').first().json;

let specContent = '';
let planContent = '';
let tasksContent = '';

for (const item of items) {
  if (item.json.blob_path?.includes('spec.md')) {
    specContent = item.json.content || '';
  } else if (item.json.blob_path?.includes('plan.md')) {
    planContent = item.json.content || '';
  } else if (item.json.blob_path?.includes('tasks.md')) {
    tasksContent = item.json.content || '';
  }
}

if (!specContent || !tasksContent) {
  throw new Error('Failed to download required artifacts');
}

// T072: Parse current task from tasks.md
const taskNumber = inputData.current_task_number;
const taskPattern = new RegExp(`- \\[ \\] T0*${taskNumber}\\s+([^\\n]+(?:\\n\\s+[^\\n-]+)*)`, 'm');
const taskMatch = tasksContent.match(taskPattern);

let currentTask = null;
if (taskMatch) {
  currentTask = taskMatch[0].trim();
} else {
  // Try alternate pattern
  const altPattern = new RegExp(`T0*${taskNumber}[^\\n]*`, 'i');
  const altMatch = tasksContent.match(altPattern);
  if (altMatch) {
    currentTask = altMatch[0].trim();
  }
}

if (!currentTask) {
  throw new Error(`Could not find task T${taskNumber} in tasks.md`);
}

return [{
  json: {
    task_id: inputData.task_id,
    current_task_number: taskNumber,
    current_task: currentTask,
    spec_content: specContent,
    plan_content: planContent,
    tasks_content: tasksContent,
    feedback: inputData.feedback
  }
}];

// T073-T074: Build implementation prompt using dev-agent.md template
const data = $input.first().json;

// T074: Context management - create summaries to stay within budget
const specSummary = data.spec_content.length > 4000 
  ? data.spec_content.substring(0, 4000) + '\n[Truncated...]'
  : data.spec_content;

const planSummary = data.plan_content.length > 3000
  ? data.plan_content.substring(0, 3000) + '\n[Truncated...]'
  : data.plan_content;

// Build the system prompt with Dev Agent implementation behavior
const systemPrompt = `You are a Senior Software Engineer implementing a feature for the autonomous dev team.

## Behavior: /speckit.implement

You are implementing task T${data.current_task_number}. Follow these rules:

1. **Read First**: Understand existing code patterns before changing anything
2. **Minimal Changes**: Implement only what the task requires - no refactoring
3. **Follow Patterns**: Match existing code style exactly
4. **Test Coverage**: Write tests for new functionality
5. **Atomic Commits**: Each logical change gets its own commit

## Git Workflow

- Branch: feat/${data.task_id}-task-${data.current_task_number}
- Commit format: type(scope): description
- PR description must reference the task

## Output Format

Your response MUST end with a YAML block:

\`\`\`yaml
status: success | blocked | error
pr_url: "https://github.com/..."
branch: "feat/${data.task_id}-task-${data.current_task_number}"
commits:
  - sha: "abc1234"
    message: "feat(scope): description"
summary: |
  Brief description of changes
deviations: []
blocked_reason: "" # only if blocked
\`\`\`

## Constraints

- Do NOT modify files outside the task scope
- Do NOT refactor unrelated code
- Do NOT add features not in the specification
- Do NOT introduce security vulnerabilities`;

// Build user prompt with context
let userPrompt = `## Current Task

**Task ID**: ${data.task_id}
**Task Number**: T${data.current_task_number}

\`\`\`
${data.current_task}
\`\`\`

## Specification Summary

${specSummary}

## Implementation Plan Summary

${planSummary}`;

// Add feedback if this is a retry
if (data.feedback) {
  userPrompt += `\n\n## Feedback to Address\n\nThis is a retry. Please address the following feedback:\n\n${data.feedback}`;
}

userPrompt += `\n\n---\n\nPlease implement this task. Create the necessary code changes, commits, and PR. End your response with the YAML status block.`;

return [{
  json: {
    task_id: data.task_id,
    current_task_number: data.current_task_number,
    prompt: userPrompt,
    system_prompt: systemPrompt,
    agent_role: 'dev'
  }
}];

// T076: Parse PR URL and commit SHA from agent output
const agentResponse = $input.first().json;
const previousData = $('Build Implementation Prompt').first().json;

if (!agentResponse.success) {
  throw new Error(`Agent Runner failed: ${agentResponse.error || 'Unknown error'}`);
}

const output = agentResponse.output || '';

// Parse YAML block from end of output
let status = 'error';
let prUrl = null;
let branch = null;
let commits = [];
let summary = '';
let deviations = [];
let blockedReason = null;

const yamlMatch = output.match(/```yaml\n([\s\S]*?)```\s*$/);
if (yamlMatch) {
  const yamlContent = yamlMatch[1];

  // Parse status
  const statusMatch = yamlContent.match(/status:\s*(\S+)/);
  if (statusMatch) {
    status = statusMatch[1].replace(/["']/g, '');
  }

  // Parse PR URL
  const prMatch = yamlContent.match(/pr_url:\s*["']?([^\s\n"']+)["']?/);
  if (prMatch) {
    prUrl = prMatch[1];
  }

  // Parse branch
  const branchMatch = yamlContent.match(/branch:\s*["']?([^\s\n"']+)["']?/);
  if (branchMatch) {
    branch = branchMatch[1];
  }

  // Parse commits (simplified)
  const commitMatches = yamlContent.matchAll(/sha:\s*["']?([^\s\n"']+)["']?/g);
  for (const match of commitMatches) {
    commits.push({ sha: match[1] });
  }

  // Parse summary
  const summaryMatch = yamlContent.match(/summary:\s*\|?\n?\s*([^\n]+)/);
  if (summaryMatch) {
    summary = summaryMatch[1].trim();
  }

  // Parse blocked reason
  const blockedMatch = yamlContent.match(/blocked_reason:\s*["']?([^\n]+)["']?/);
  if (blockedMatch && blockedMatch[1].trim()) {
    blockedReason = blockedMatch[1].trim();
  }
}

return [{
  json: {
    task_id: previousData.task_id,
    current_task_number: previousData.current_task_number,
    status,
    pr_url: prUrl,
    branch,
    commits,
    summary,
    deviations,
    blocked_reason: blockedReason,
    raw_output: output
  }
}];

// T077: Prepare task envelope update with PR info
const data = $input.first().json;

return [{
  json: {
    operation: 'read',
    task_id: data.task_id,
    pr_url: data.pr_url,
    branch: data.branch,
    commits: data.commits,
    summary: data.summary,
    current_task_number: data.current_task_number
  }
}];

// T077: Update task envelope with PR info
const readResult = $input.first().json;
const updateData = $('Prepare Envelope Read').first().json;

if (!readResult.success) {
  throw new Error(`Failed to read task envelope: ${readResult.error || 'Unknown error'}`);
}

const envelope = readResult.task_envelope;
const now = new Date().toISOString();

// Update implementation phase with PR info
envelope.phases.implementation = {
  ...envelope.phases.implementation,
  status: 'in_progress',
  current_task: updateData.current_task_number,
  pr_url: updateData.pr_url,
  branch: updateData.branch,
  commits: [...(envelope.phases.implementation?.commits || []), ...updateData.commits.map(c => c.sha)]
};

envelope.version = (envelope.version || 1) + 1;

return [{
  json: {
    operation: 'update',
    task_id: updateData.task_id,
    task_envelope: envelope
  }
}];

// Return success result
const updateData = $('Prepare Envelope Read').first().json;

return [{
  json: {
    success: true,
    task_id: updateData.task_id,
    status: 'success',
    pr_url: updateData.pr_url,
    branch: updateData.branch,
    commits: updateData.commits,
    summary: updateData.summary
  }
}];

// T078: Handle blocked status - escalate to human
const data = $input.first().json;

return [{
  json: {
    success: false,
    task_id: data.task_id,
    status: 'blocked',
    blocked_reason: data.blocked_reason,
    needs_human: true
  }
}];

// T078: Handle error status
const data = $input.first().json;

return [{
  json: {
    success: false,
    task_id: data.task_id,
    status: 'error',
    error: data.raw_output?.substring(0, 500) || 'Implementation failed'
  }
}];

// Handle workflow errors
const error = $input.first().json;
const inputData = $('Sub-Workflow Trigger').first().json;

return [{
  json: {
    success: false,
    task_id: inputData?.task_id || 'unknown',
    status: 'error',
    error: error.message || 'Unknown error in Dev Implementation workflow'
  }
}];
